def transform_data(test_list):
    header = ['']
    rows_dict = {}

    for item in test_list:
        name, subject, score = item

        if subject not in header:
            header.append(subject)

        if name not in rows_dict:
            rows_dict[name] = [name] + [''] * (len(header) - 1)

        subject_index = header.index(subject)
        if len(rows_dict[name]) < len(header):
            rows_dict[name].extend([''] * (len(header) - len(rows_dict[name])))

        rows_dict[name][subject_index] = score

    result = []
    result.append(header)
    for row in rows_dict.values():
        result.append(row)

    return result

